SELECT ROWNUM AS rnum, a.pern_no, A.NAME, a.sex_code, a.dept_code, h.detail_code_name as dept_name, a.post_code, b.detail_code_name AS post_name, a.join_code, c.detail_code_name AS join_name, a.employ_type, d.detail_code_name AS employ_name, a.salary_code, e.detail_code_name AS salary_name, a.wages_amt, a.join_date, a.retr_date, a.work_area, f.detail_code_name AS work_area_name, a.phone_no, a.mutual_yn, a.pay_grade, g.detail_code_name AS pay_name FROM hpa100t a, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H005' ) b, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H012' ) c, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H024' ) d, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H028' ) e, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H003' ) f, (SELECT * FROM bco100t WHERE bco100t.com_code = 'H004' ) g, (SELECT * FROM bco100t_bak20210917 WHERE com_code = 'H002' ) h WHERE a.post_code = b.detail_code (+) AND a.join_code = c.detail_code (+) AND a.employ_type = d.detail_code (+) AND a.salary_code = e.detail_code (+) AND a.work_area = f.detail_code (+) AND a.pay_grade = g.detail_code (+) AND a.dept_code = h.detail_code (+) AND a.mutual_yn is not null
AND a.retr_date is NULL
AND a.retr_date is NOT NULL
AND (SUBSTR(a.dept_code, 0, 2) = #{deptCode} OR a.dept_code = #{deptCode})
AND a.post_code = #{postCode}
AND a.pay_grade = #{payGrade}
AND a.salary_code = #{salaryCode}
AND (a.pern_no LIKE '%' || #{pernNo} || '%' OR a.name LIKE '%' || #{pernNo} || '%')

