AND a.retr_date is NULL
AND SUBSTR(a.join_date, 0, 4) = #{joinYear}
AND SUBSTR(a.join_date, 5, 2) = #{joinMonth}
AND a.retr_date is NOT NULL
AND SUBSTR(a.retr_date, 0, 4) = #{joinYear}
AND SUBSTR(a.retr_date, 5, 2) = #{joinMonth}
